const express = require('express');
const {default:mongoose}=require('mongoose');
const routerApi=require('./routes');
const app = express();
//puerto por el que va a escuchar
const port=process.env.PORT||3000;
require("dotenv").config();
//alt shift+f identacion
//recuest para verificar si el puerto estÃ¡ respondiendo
app.listen(port, () => {
  console.log("listening the port", port);
});
//middleware for http to show in json
app.use(express.json())
//conexion con BD mongo
mongoose
    .connect(process.env.MONGODB_CONNECTION_STRING)
    .then(()=>console.log("connected with mongo"))
    .catch((error)=>console.log(error));
//permite pasar el app
routerApi(app);

