const mongoose = require('mongoose');
const JediSchema = mongoose.Schema({
  name: {
    type: String,
    require: true,
  },
  image: {
    type: String,
    require: true,
  },
  generalities:{
    type: Object,
    require: true,
    info: {
      type: Object,
      require: true,
      lightsaber: {
        type: String,
        require: true,
      },
      information: {
        type: String,
        require: true,
      },
    },
  },
  condition: {
      type: Object,
      require: true,
      place: {
        type: Object,
        require: true,
        appearance: {
          type: String,
          require: true,
        },
      },
      alive: {
        type: Boolean,
        require: true,
      },
  },
  
});

module.exports = mongoose.model('JediCollection', JediSchema);
